import React, { useState } from "react";
import "./SearchModal.css";

const SearchModal = ({ onClose }) => {
  const [period, setPeriod] = useState("Weekly"); // Daily, Weekly, Monthly
  const [showDropdown, setShowDropdown] = useState(false);

  // ���� ������ (������ API�� �ҷ��� �� ����)
  const searchData = {
    Daily: ["K-pop Concert", "Art Festival", "Drama Event", "Jazz Night", "Indie Film", "Food Fair", "Fashion Show", "Theater Play", "Book Fair", "Dance Battle"],
    Weekly: ["Musical", "Street Festival", "Live Painting", "Classical Concert", "Comedy Show", "Board Game Fair", "Magic Show", "Sports Event", "Poetry Night", "VR Experience"],
    Monthly: ["International Film Festival", "Traditional Market", "Halloween Parade", "Fireworks Show", "Photography Exhibition", "Startup Fair", "Handmade Fair", "Wine Tasting", "Opera Night", "Christmas Market"]
  };

  return (
    <div className="search-modal-overlay">
      <div className="search-modal">
        {/* �˻�â */}
        <div className="search-bar">
          <input type="text" placeholder="Search..." />
          <button>Go</button>
        </div>

        {/* Top �˻��� ��� */}
        <div className="search-header">
          <h3>Top</h3>
          <div className="dropdown">
            <span
              className="dropdown-toggle"
              onClick={() => setShowDropdown(!showDropdown)}
            >
              {period} ��
            </span>
            {showDropdown && (
              <div className="dropdown-menu">
                {["Daily", "Weekly", "Monthly"].map((item) => (
                  <div
                    key={item}
                    onClick={() => {
                      setPeriod(item);
                      setShowDropdown(false);
                    }}
                  >
                    {item}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* �ǽð� �˻��� ����Ʈ */}
        <ul className="search-list">
          {searchData[period].map((term, idx) => (
            <li key={idx}>
              <span className="rank">{idx + 1}.</span>
              <span className="term">{term}</span>
            </li>
          ))}
        </ul>

        <button className="close-btn" onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default SearchModal;
