import './foodmap.css';
import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Foodmap = () => {
    const navigate = useNavigate();

    useEffect(() => {
        const script = document.createElement("script");
        script.src = `//dapi.kakao.com/v2/maps/sdk.js?appkey=50d60c48f372a7f105505d4140b8f96d&autoload=false`;
        script.async = true;

        let goBackButton;

        script.onload = async () => {
            window.kakao.maps.load(async () => {
                // ✅ JSON 파일들 fetch
                const geojson = await fetch('/seoul.geojson').then(res => res.json());
                const districtsJson = await fetch('/seoul_districts.geojson').then(res => res.json());
                const centerData = await fetch('/seoul_districts_topo.json').then(res => res.json());

                const mapContainer = document.getElementById("politic-map");
                if (!mapContainer) return;

                const mapOption = {
                    center: new window.kakao.maps.LatLng(37.566826, 126.9786567),
                    level: 9,
                };

                const map = new window.kakao.maps.Map(mapContainer, mapOption);
                const customOverlay = new window.kakao.maps.CustomOverlay({});
                const infowindow = new window.kakao.maps.InfoWindow({ removable: true });

                let regionPolygons = [];
                let dongPolygons = [];

                const displayDongAreas = (dongData) => {
                    dongData.forEach((dong) => {
                        const draw = (coords) => {
                            const path = coords.map(coord => new window.kakao.maps.LatLng(coord[1], coord[0]));
                            const polygon = new window.kakao.maps.Polygon({
                                map,
                                path,
                                strokeWeight: 2,
                                strokeColor: "#5C5B5C",
                                strokeOpacity: 0.8,
                                fillColor: "#CACACB",
                                fillOpacity: 0.7,
                            });
                            dongPolygons.push(polygon);
                            addDongPolygonEvents(polygon, dong);
                        };

                        if (dong.geometry.type === "Polygon") {
                            draw(dong.geometry.coordinates[0]);
                        } else if (dong.geometry.type === "MultiPolygon") {
                            dong.geometry.coordinates.forEach(part => draw(part[0]));
                        }
                    });
                };

                const addDongPolygonEvents = (polygon, dong) => {
                    window.kakao.maps.event.addListener(polygon, "mouseover", (e) => {
                        polygon.setOptions({ fillColor: "#b29ddb" });
                        customOverlay.setPosition(e.latLng);
                        customOverlay.setMap(map);
                    });

                    window.kakao.maps.event.addListener(polygon, "mouseout", () => {
                        polygon.setOptions({ fillColor: "#CACACB" });
                        customOverlay.setMap(null);
                    });

                    window.kakao.maps.event.addListener(polygon, "click", (e) => {
                        const districtName = dong.properties.SIG_KOR_NM;

                        const content = document.createElement("div");
                        content.className = "infowindow-content";
                        content.innerHTML = `
                            <p><b>${dong.properties.DONG_KOR_NM}</b>의 정치인 조회</p>
                            <div class="infowindow-button-container">
                                <button id="mayor-btn" class="infowindow-button">구청장 조회</button>
                                <button id="representative-btn" class="infowindow-button">국회의원 조회</button>
                            </div>`;

                        infowindow.setContent(content);
                        infowindow.setPosition(e.latLng);
                        infowindow.setMap(map);

                        content.querySelector("#mayor-btn").addEventListener("click", () => {
                            if (districtName !== "종로구") {
                                alert("서비스 준비중입니다.");
                                return;
                            }
                            navigate("/yunji");
                        });

                        content.querySelector("#representative-btn").addEventListener("click", () => {
                            if (districtName !== "종로구") {
                                alert("서비스 준비중입니다.");
                                return;
                            }
                            navigate("/seoin");
                        });
                    });
                };

                const displayArea = (coordinates, name) => {
                    const path = coordinates.map(coord => new window.kakao.maps.LatLng(coord[1], coord[0]));

                    const polygon = new window.kakao.maps.Polygon({
                        map,
                        path,
                        strokeWeight: 2,
                        strokeColor: "#004c80",
                        strokeOpacity: 0.8,
                        fillColor: "#fff",
                        fillOpacity: 0.7,
                    });

                    regionPolygons.push(polygon);

                    window.kakao.maps.event.addListener(polygon, "mouseover", (e) => {
                        polygon.setOptions({ fillColor: "#cfc2e9" });
                        customOverlay.setPosition(e.latLng);
                        customOverlay.setMap(map);
                    });

                    window.kakao.maps.event.addListener(polygon, "mouseout", () => {
                        polygon.setOptions({ fillColor: "#fff" });
                        customOverlay.setMap(null);
                    });

                    window.kakao.maps.event.addListener(polygon, "click", () => {
                        regionPolygons.forEach(p => p.setMap(null));
                        regionPolygons = [];

                        const center = centerData.centers.find(c => c.name === name);
                        if (center) {
                            map.setCenter(new window.kakao.maps.LatLng(center.lat, center.lng));
                        }

                        const dongData = districtsJson.features.filter(dong => dong.properties.SIG_KOR_NM === name);
                        if (dongData.length > 0) {
                            map.setLevel(7);
                            displayDongAreas(dongData);
                            addGoBackButton();
                        } else {
                            alert(`${name}의 동 정보가 없습니다.`);
                        }
                    });
                };

                const addGoBackButton = () => {
                    if (goBackButton) return;
                    goBackButton = document.createElement("button");
                    goBackButton.innerText = "구 다시 선택하기";
                    goBackButton.className = "back-button";
                    goBackButton.onclick = () => resetToRegions();
                    document.body.appendChild(goBackButton);
                };

                const resetToRegions = () => {
                    dongPolygons.forEach(p => p.setMap(null));
                    dongPolygons = [];
                    infowindow.close();

                    map.setCenter(new window.kakao.maps.LatLng(37.566826, 126.9786567));
                    map.setLevel(9);

                    if (goBackButton) {
                        goBackButton.remove();
                        goBackButton = null;
                    }

                    geojson.features.forEach(feature => {
                        const coordinates = feature.geometry.coordinates[0];
                        const name = feature.properties.SIG_KOR_NM;
                        displayArea(coordinates, name);
                    });
                };

                geojson.features.forEach(feature => {
                    const coordinates = feature.geometry.coordinates[0];
                    const name = feature.properties.SIG_KOR_NM;
                    displayArea(coordinates, name);
                });
            });
        };

        document.head.appendChild(script);

        return () => {
            if (goBackButton) {
                goBackButton.remove();
            }
        };
    }, [navigate]);

    return (
    <div>
    <h1>우리 지역 근처 맛집 찾기</h1>
    <div id="politic-map"></div>
    </div>
    );
};

export default Foodmap;
