import React, { useState } from 'react';
import './Topnav.css';

const Topnav = () => {
  const [showSearch, setShowSearch] = useState(false);
  const [query, setQuery] = useState('');

  const handleSearchToggle = () => {
    setShowSearch(prev => !prev);
    setQuery('');
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (query.trim()) {
      alert(`üîé Searching for: ${query}`);
      // Ïã§Ï†ú Í≤ÄÏÉâ Î°úÏßÅÏùÄ ÎùºÏö∞ÌÑ∞ Ïù¥Îèô ÎòêÎäî ÌïÑÌÑ∞ÎßÅ Ìï®ÏàòÎ°ú Ï≤òÎ¶¨
    }
  };

  return (
    <header className="topnav">
      <div className="menu-toggle">‚ò∞</div>

      <h1 className="logo">CultureNow</h1>

      <div className="topnav-right">
        <span
          className="search-icon"
          role="img"
          aria-label="search"
          onClick={handleSearchToggle}
        >
          üîç
        </span>
        <button className="lang-btn" onClick={() => alert('Ïñ∏Ïñ¥ Î≥ÄÍ≤Ω')}>üåê</button>
      </div>

      {showSearch && (
        <form onSubmit={handleSearchSubmit} className="search-bar">
          <input
            type="text"
            placeholder="Search content..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
          />
          <button type="submit">Go</button>
        </form>
      )}
    </header>
  );
};

export default Topnav;
