import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Topnav from "../components/Topnav";   // 상단바 사용
import "./Main.css";

const CATS = [
  { label: "공연/연극", slug: "theater" },
  { label: "콘서트",   slug: "concert" },
  { label: "스포츠",   slug: "sports" },
  { label: "전시/행사", slug: "exhibition" },
  { label: "맛집/카페", slug: "food" },
  { label: "아동/가족", slug: "kids" },
  { label: "관광",     slug: "tour" },
  { label: "오늘의 추천", slug: "today" },
];

function Hero() {
  const [idx, setIdx] = useState(0);      // 간단 슬라이드 인디케이터
  const total = 5;
  return (
    <header className="hero">
      <h1>추천 만한 메인 이벤트</h1>
      <p>Live Local. Explore Korea.</p>
      <div className="slide-indicator">
        <button onClick={()=>setIdx((i)=>(i-1+total)%total)}>‹</button>
        <span>{idx+1}/{total}</span>
        <button onClick={()=>setIdx((i)=>(i+1)%total)}>›</button>
      </div>
    </header>
  );
}

function CategoryGrid({ onPick }) {
  return (
    <section className="section">
      <div className="cat-grid">
        {CATS.map(c=>(
          <button key={c.slug} className="cat" onClick={()=>onPick(c.slug)}>
            <div className="cat-box" />
            <div className="cat-label">{c.label}</div>
          </button>
        ))}
      </div>
    </section>
  );
}

function MiniCalendar() {
  const today = new Date();
  const [m, setM] = useState(today.getMonth());
  const [y, setY] = useState(today.getFullYear());
  const firstDow = new Date(y, m, 1).getDay();
  const days = new Date(y, m+1, 0).getDate();
  const cells = Array.from({length:firstDow},()=>null).concat(
    Array.from({length:days},(_,i)=>i+1)
  );

  return (
    <section className="cal">
      <div className="cal-head">
        <button onClick={()=>{ const d=new Date(y,m-1,1); setY(d.getFullYear()); setM(d.getMonth()); }}>‹</button>
        <h3>{y}.{String(m+1).padStart(2,"0")}</h3>
        <button onClick={()=>{ const d=new Date(y,m+1,1); setY(d.getFullYear()); setM(d.getMonth()); }}>›</button>
      </div>
      <div className="cal-grid">
        {["Sun","Mon","Tue","Wed","Thu","Fri","Sat"].map(d=>
          <div key={d} className="cal-cell cal-dow">{d}</div>
        )}
        {cells.map((d,i)=>(
          <div key={i} className="cal-cell">{d && <span className="cal-day">{d}</span>}</div>
        ))}
      </div>
    </section>
  );
}

export default function Main(){
  const navigate = useNavigate();
  const goGenre = (slug)=> navigate(`/genre?category=${slug}`);

  return (
    <div className="main-page">
      <Topnav />
      <div className="spacer" />
      <main className="main-container">
        <Hero />
        <CategoryGrid onPick={goGenre} />
        <MiniCalendar />
      </main>
    </div>
  );
}
