import React, { useState } from "react";
import "./Signup.css";

const COUNTRY_OPTIONS = [
  "Korea", "USA", "Japan", "China", "Germany",
  "France", "Canada", "UK", "Spain", "Australia"
];

const LANGUAGE_OPTIONS = [
  "Korean", "English", "Japanese", "Chinese",
  "German", "French", "Spanish", "Portuguese", "Russian", "Arabic"
];

export default function Signup() {
  const [form, setForm] = useState({
    name: "",
    gender: null,            // 0 or 1
    userid: "",
    password: "",
    country: "",
    language: "",
    age: "",
    interestTag: "",
  });

  const update = (key, value) => setForm(prev => ({ ...prev, [key]: value }));

  const canSubmit =
    form.name &&
    (form.gender === 0 || form.gender === 1) &&
    form.userid &&
    form.password &&
    form.country &&
    form.language &&
    String(form.age).match(/^\d+$/); // 정수 확인

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!canSubmit) return;

    const payload = {
      name: form.name.trim(),
      gender: Number(form.gender),             // 0 or 1
      userid: form.userid.trim(),
      password: form.password,                 // 데모용: 그대로 보냄 (실서비스는 해싱!)
      country: form.country,
      language: form.language,
      age: parseInt(form.age, 10),
      interestTag: form.interestTag.trim(),
    };

    console.log("signup payload:", payload);
    alert("회원가입 데이터가 콘솔에 출력되었습니다.");
    // TODO: 실제 API 연동 시 fetch/axios로 POST
  };

  return (
    <div className="signup-wrap">
      <h2>회원가입</h2>

      <form className="signup-form" onSubmit={handleSubmit}>
        <label>
          이름
          <input
            type="text"
            value={form.name}
            onChange={(e) => update("name", e.target.value)}
            placeholder="이름을 입력하세요"
          />
        </label>

        <div className="field">
          <span className="field-label">성별</span>
          <div className="toggle-group">
            <button
              type="button"
              className={`toggle ${form.gender === 0 ? "active" : ""}`}
              onClick={() => update("gender", 0)}
            >
              남자
            </button>
            <button
              type="button"
              className={`toggle ${form.gender === 1 ? "active" : ""}`}
              onClick={() => update("gender", 1)}
            >
              여자
            </button>
          </div>
        </div>

        <label>
          아이디
          <input
            type="text"
            value={form.userid}
            onChange={(e) => update("userid", e.target.value)}
            placeholder="아이디를 입력하세요"
            autoComplete="username"
          />
        </label>

        <label>
          비밀번호
          <input
            type="password"
            value={form.password}
            onChange={(e) => update("password", e.target.value)}
            placeholder="비밀번호를 입력하세요"
            autoComplete="new-password"
          />
        </label>

        <div className="field">
          <span className="field-label">국가</span>
          <div className="toggle-group scrollable">
            {COUNTRY_OPTIONS.map((c) => (
              <button
                type="button"
                key={c}
                className={`toggle ${form.country === c ? "active" : ""}`}
                onClick={() => update("country", c)}
              >
                {c}
              </button>
            ))}
          </div>
        </div>

        <div className="field">
          <span className="field-label">언어</span>
          <div className="toggle-group scrollable">
            {LANGUAGE_OPTIONS.map((l) => (
              <button
                type="button"
                key={l}
                className={`toggle ${form.language === l ? "active" : ""}`}
                onClick={() => update("language", l)}
              >
                {l}
              </button>
            ))}
          </div>
        </div>

        <label>
          나이
          <input
            type="number"
            inputMode="numeric"
            value={form.age}
            onChange={(e) => update("age", e.target.value)}
            placeholder="예: 23"
            min="0"
          />
        </label>

        <label>
          관심 태그
          <input
            type="text"
            value={form.interestTag}
            onChange={(e) => update("interestTag", e.target.value)}
            placeholder="예: K-pop, Food, Museum"
          />
        </label>

        <button className="submit" type="submit" disabled={!canSubmit}>
          회원가입
        </button>
      </form>
    </div>
  );
}
