import React from "react";
import { useNavigate } from "react-router-dom";
import "./Main.css";

// 더미 데이터 (필요 시 교체)
const categories = ["영화", "뮤지컬", "연극", "전시회", "마켓", "축제"];
const events = [
  { id: 1, title: "Open-air Cinema", date: "2025-08-10", where: "Seoul" },
  { id: 2, title: "Musical Night",   date: "2025-08-12", where: "Busan" },
  { id: 3, title: "Street Market",   date: "2025-08-21", where: "Daegu" },
];
const rankings = [
  { id: "r1", title: "Indie Film Fest", score: 98 },
  { id: "r2", title: "Modern Art Expo", score: 93 },
  { id: "r3", title: "Jazz Night",      score: 90 },
];

// 내부 컴포넌트들 (파일 분리 없음)
function CategoryMenu({ onSelect }) {
  return (
    <div className="category-wrap">
      {categories.map((c) => (
        <button key={c} className="category-btn" onClick={() => onSelect(c)}>
          {c}
        </button>
      ))}
    </div>
  );
}

function EventList({ items }) {
  return (
    <section className="section">
      <h3>Recommended Events</h3>
      <div className="grid">
        {items.map((e) => (
          <article key={e.id} className="card">
            <h4 className="card-title">{e.title}</h4>
            <div className="card-meta">
              {e.where} · {e.date}
            </div>
            <button className="badge">View</button>
          </article>
        ))}
      </div>
    </section>
  );
}

function RankingList({ items }) {
  return (
    <section className="section">
      <h3>Weekly Top</h3>
      <div className="grid">
        {items.map((r, i) => (
          <article key={r.id} className="card">
            <h4 className="card-title">
              {i + 1}. {r.title}
            </h4>
            <div className="card-meta">Score: {r.score}</div>
          </article>
        ))}
      </div>
    </section>
  );
}

export default function Main() {
  const navigate = useNavigate();
  const goGenre = (cat) => navigate(`/genre?category=${encodeURIComponent(cat)}`);

  return (
    <div className="main-page">
      <main className="main-container">
        <header className="main-hero">
          <h2>추천 만한 메인 이벤트</h2>
          <p>Discover local experiences near you</p>
        </header>

        <CategoryMenu onSelect={goGenre} />

        <EventList items={events} />
        <RankingList items={rankings} />
      </main>
    </div>
  );
}
