// Main.jsx
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Topnav from "../components/Topnav";
import posters from "../Genre/postersData";
import "./Main.css";
import SearchModal from "./SearchModal";
import "./TopSearchBox.css"; // 실검창 스타일

const CATS = [
  { label: "공연/연극", slug: "theater" },
  { label: "콘서트", slug: "concert" },
  { label: "스포츠", slug: "sports" },
  { label: "전시/행사", slug: "exhibition" },
  { label: "맛집/카페", slug: "food" },
  { label: "아동/가족", slug: "kids" },
  { label: "관광", slug: "tour" },
  { label: "오늘의 추천", slug: "today" },
];

// 실검창 컴포넌트
function TopSearchBox({ period, setPeriod }) {
  const keywords = [
    "페스티벌 2025",
    "서울 콘서트",
    "뮤지컬 예매",
    "야구 경기 일정",
    "전시회 추천",
    "맛집 베스트",
    "가족 여행지",
    "불꽃놀이",
    "카페 핫플",
    "스포츠 경기표"
  ];

  return (
    <div className="top-search-box">
      <div className="top-search-header">
        <span>Top {period} Search</span>
        <select
          value={period}
          onChange={(e) => setPeriod(e.target.value)}
          className="period-select"
        >
          <option value="Weekly">Weekly</option>
          <option value="Daily">Daily</option>
          <option value="Monthly">Monthly</option>
        </select>
      </div>
      <ul className="top-search-list">
        {keywords.map((word, idx) => (
          <li key={idx}>
            <span className="rank">{idx + 1}.</span> {word}
          </li>
        ))}
      </ul>
    </div>
  );
}

// Hero
function Hero() {
  const [idx, setIdx] = useState(0);
  const total = posters.length;

  useEffect(() => {
    const timer = setInterval(() => {
      setIdx((prev) => (prev + 1) % total);
    }, 5000);
    return () => clearInterval(timer);
  }, [total]);

  const visible = [
    posters[idx % total],
    posters[(idx + 1) % total],
    posters[(idx + 2) % total],
  ];

  return (
    <header className="hero">
      <h1>추천 만한 메인 이벤트</h1>
      <p>Live Local. Explore Korea.</p>

      <div className="poster-carousel">
        {visible.map((p) => (
          <div key={p.id} className="poster-card">
            <img src={p.image} alt={p.title} className="poster-img" />
            <div className="poster-title">{p.title}</div>
            <div className="poster-info">{p.category}</div>
          </div>
        ))}
      </div>

      <div className="slide-indicator">
        <button onClick={() => setIdx((i) => (i - 1 + total) % total)}>‹</button>
        <span>{idx + 1}/{total}</span>
        <button onClick={() => setIdx((i) => (i + 1) % total)}>›</button>
      </div>
    </header>
  );
}

// CategoryGrid
function CategoryGrid({ onPick }) {
  return (
    <section className="section">
      <div className="cat-grid">
        {CATS.map((c) => (
          <button key={c.slug} className="cat" onClick={() => onPick(c.slug)}>
            <div className="cat-box" />
            <div className="cat-label">{c.label}</div>
          </button>
        ))}
      </div>
    </section>
  );
}

// MiniCalendar
function MiniCalendar() {
  const today = new Date();
  const [m, setM] = useState(today.getMonth());
  const [y, setY] = useState(today.getFullYear());
  const firstDow = new Date(y, m, 1).getDay();
  const days = new Date(y, m + 1, 0).getDate();
  const cells = Array.from({ length: firstDow }, () => null).concat(
    Array.from({ length: days }, (_, i) => i + 1)
  );

  return (
    <section className="cal">
      <div className="cal-head">
        <button
          onClick={() => {
            const d = new Date(y, m - 1, 1);
            setY(d.getFullYear());
            setM(d.getMonth());
          }}
        >
          ‹
        </button>
        <h3>{y}.{String(m + 1).padStart(2, "0")}</h3>
        <button
          onClick={() => {
            const d = new Date(y, m + 1, 1);
            setY(d.getFullYear());
            setM(d.getMonth());
          }}
        >
          ›
        </button>
      </div>
      <div className="cal-grid">
        {["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"].map((d) => (
          <div key={d} className="cal-cell cal-dow">
            {d}
          </div>
        ))}
        {cells.map((d, i) => (
          <div key={i} className="cal-cell">
            {d && <span className="cal-day">{d}</span>}
          </div>
        ))}
      </div>
    </section>
  );
}

export default function Main() {
  const navigate = useNavigate();
  const goGenre = (slug) => navigate(`/genre?category=${slug}`);

  const [showSearch, setShowSearch] = useState(false);
  const [period, setPeriod] = useState("Weekly");

  return (
    <div className="main-page">
      <Topnav onSearchClick={() => setShowSearch(true)} />

      {showSearch && (
        <>
          <SearchModal onClose={() => setShowSearch(false)} />
          <TopSearchBox period={period} setPeriod={setPeriod} />
        </>
      )}

      <div className="spacer" />
      <main className="main-container">
        <Hero />
        <CategoryGrid onPick={goGenre} />
        <MiniCalendar />
      </main>
    </div>
  );
}
