import React, { useState } from 'react';
import './Signup.css';

const COUNTRIES = ['Korea','USA','Japan','China','Germany','France','Canada','UK','Spain','Australia'];
const LANGUAGES = ['Korean','English','Japanese','Chinese','German','French','Spanish','Portuguese','Russian','Arabic'];
const TAGS = ['K-pop','Food','Museum','Nightlife','Nature','History','Market','Cafe','Festival'];

export default function Signup() {
  const [country, setCountry] = useState('Korea');
  const [langs, setLangs] = useState(['Korean']);
  const [age, setAge] = useState('');
  const [tags, setTags] = useState([]);

  const toggle = (list, setter, value) =>
    setter(prev => prev.includes(value) ? prev.filter(v => v !== value) : [...prev, value]);

  const onSubmit = (e) => {
    e.preventDefault();
    console.log({ country, langs, age, tags });
    alert('저장되었습니다!');
  };

  return (
    <div className="signup-wrap">
      <h2>회원 정보</h2>
      <form className="signup-form" onSubmit={onSubmit}>
        <div className="field">
          <span className="field-label">국가</span>
          <div className="toggle-group">
            {COUNTRIES.map(c => (
              <button
                type="button"
                key={c}
                className={`toggle ${country === c ? 'active' : ''}`}
                onClick={() => setCountry(c)}
              >
                {c}
              </button>
            ))}
          </div>
        </div>

        <div className="field">
          <span className="field-label">언어</span>
          <div className="toggle-group">
            {LANGUAGES.map(l => (
              <button
                type="button"
                key={l}
                className={`toggle ${langs.includes(l) ? 'active' : ''}`}
                onClick={() => toggle(langs, setLangs, l)}
              >
                {l}
              </button>
            ))}
          </div>
        </div>

        <div className="field">
          <span className="field-label">나이</span>
          <input
            type="number"
            placeholder="예: 23"
            value={age}
            onChange={e => setAge(e.target.value)}
          />
        </div>

        <div className="field">
          <span className="field-label">관심 태그</span>
          <div className="chip-group">
            {TAGS.map(t => (
              <button
                type="button"
                key={t}
                className={`chip ${tags.includes(t) ? 'selected' : ''}`}
                onClick={() => toggle(tags, setTags, t)}
              >
                {t}
              </button>
            ))}
          </div>
        </div>

        <button className="submit" disabled={!country || langs.length === 0 || !age}>
          저장
        </button>
      </form>
    </div>
  );
}
